def unknown_hostname_motd():
    name = b'Sayetta Network'
    motd = b'Invalid hostname'

    message = b'\xd9\x1d\x00\xd6\x1d{"version":{"name":"' + name + b'","protocol":-1},"players":{"max":0,"online":0,"sample":null},"description":{"text":"' + motd + b'"},"favicon":"data:image/png;base64,"}'

    return message